import React, { useState } from "react";

function LittleEndian(){
    const [inputValue, setInputValue] = useState("");

    // const changingValue = (event) => {
    //     console.log(event);
    //     setInputValue(event.target.value)
    // }

    const changingValue = () => {
        this.setState({ value: this.state.input });
    };

    return (
        <div className="littleEndianArea stringDiv">
            <textarea id="stingCalculator" name="stingCalculator" rows="4" cols="50" value={inputValue}/>
            <button onClick={(event) => this.changingValue(event)}>SUBMIT</button>
            <br />
            <span>{inputValue}</span>
        </div>
    );
}

export default LittleEndian;
