import './App.css';
import Do from './Notes/Do';
import Re from './Notes/Re';
import Mi from './Notes/Mi';
import Fa from './Notes/Fa';
import Sol from './Notes/Sol';
import La from './Notes/La';
import Si from './Notes/Si';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

const App = () => {

  const [note, setNote] = useState("");
  const [count, setCount] = useState(0);

  const noteClicked = (newNote) => {
    setNote(newNote);
  }

  //useEffect statei takip eder

  // useEffect(() => {
  //   setInterval(() => {
  //     setCount((n) => n+1);
  //   }, 1000);
  // }, []);

  //componentdidmount
  //componentunmount
  useEffect(() => {
    let unmounted = false;
    console.log("bir")
    if(!unmounted){
      console.log("iki");
    }

    return (() => {
      unmounted = true;
      console.log("uc");
    })
  }, []);

  //componentdidupdate
  useEffect(() => {
    console.log("ahmet");
  }, [note]);

  return (
    <div>
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/do">Do</Link>
              </li>
              <li>
                <Link to="/re">Re</Link>
              </li>
              <li>
                <Link to="/mi">Mi</Link>
              </li>
              <li>
                <Link to="/fa">Fa</Link>
              </li>
            </ul>
          </nav>

          <Switch>
            <Route path="do" component="Do"></Route>
            <Route path="re" component="Re"></Route>
            <Route path="mi" component="Mi"></Route>
            <Route path="fa" component="Fa"></Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
