import React, { useState } from "react";

const StringCalculator = () => {

    const [inputValue, setInputValue] = useState("merhaba");

    return (
        <div className="stringDiv">
            <label htmlFor="stingCalculator">Review of W3Schools:</label>

            <textarea id="stingCalculator" name="stingCalculator" rows="4" cols="50" value={inputValue} onChange={(event) => setInputValue(event.target.value)}/>
        </div>
    )
}

export default StringCalculator;
